$(function() {
  $('a[href*="#"]:not([href="#"])').click(function(e) {
    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
      if (target.length) {
        $('html, body').animate({
          scrollTop: target.offset().top
        }, 600);
        e.preventDefault();
      }
    }
  });
});

// click outside of modal to hide
$(window).click(function(e) {
  if((!$(event.target).parents(".modal.show").is(".modal.show")) && $(".modal.show").length > 0) {
    $(".modal.show").removeClass("show");
    $(".modal-scrim.show").removeClass("show");
    $("#notifications .notification.unread").removeClass("unread");
  }

  if((!$(event.target).parents("#search-container.expand #search").is("#search-container.expand #search")) && $("#search-container.expand #search").length > 0) {
    angular.element('#search-container').scope().has_searched = false;
    angular.element('#search-container').scope().suggesting_question = false;
    angular.element('#search-container').scope().$apply();
  }
});

// press escape close modal
$(document).keyup(function(e) {
     if (e.keyCode == 27) {
        $(".modal.show").removeClass("show");
        $(".modal-scrim.show").removeClass("show");
        $("#notifications .notification.unread").removeClass("unread");

        if($("#search-container.expand #search").length > 0) {
          angular.element('#search-container').scope().has_searched = false;
          angular.element('#search-container').scope().suggesting_question = false;
          angular.element('#search-container').scope().$apply();
        }
    }
});

$(document).ready(function() {
  new ClipboardJS('.btn');

  $('.ask-question-input').blur(function() {
    $('body').removeClass("ask-question-focus");
    $(this).attr("placeholder", "Ask a question...");
  })
  .focus(function() {
    $("body").addClass("ask-question-focus");
    $(this).attr("placeholder", "A little bit of context goes a long way...");
  });

  $(document).on("click", ".modal-activate", function(e) {
    $(".modal.show").removeClass("show");
    $(".modal-scrim.show").removeClass("show");
    var modal_selector = $(this).attr('data-modal');
    $('.modal-'+modal_selector+'').addClass("show");

    if ($('.modal-'+modal_selector+'').hasClass("modal-show-scrim")) {
        $(".modal-scrim").addClass("show");
    }

    // share positioning near the button
    if (modal_selector == "share-action") {
        var action_short_url = $(this).attr('data-action-short-url');
        var action_long_url = $(this).attr('data-action-long-url');
        var share_text = $(this).attr('data-share-text');

        $(".modal-share-action .btn--copy").attr("data-clipboard-text", action_short_url);
        $(".modal-share-action .btn--twitter").attr("href", "https://twitter.com/intent/tweet?url=" + action_short_url + "&text=" + share_text + " @askplaybook");
        $(".modal-share-action .btn--facebook").attr("onclick", "FB.ui({method: 'share', href: '" + action_long_url + "',}, function(response){});");

        var left = $(this).offset().left;
        var top = $(this).offset().top;
        $('.modal-'+modal_selector+'').css({top: top + 6, left: left + 8});
    }

    if (modal_selector == "contribute-actions-mobile") {
			var left = $(this).offset().left;
			var top = $(this).offset().top;
			$('.modal-'+modal_selector+'').css({top: top + 6, left: left + 8});
		}

    e.stopPropagation();
  });

  $(".modal .btn--copy").click(function(e) {
    $(this).closest(".modal").removeClass("show");
    $(".modal-scrim.show").removeClass("show");
    $("#notifications .notification.unread").removeClass("unread");
  });

  $(".modal .close").click(function(e) {
    $(this).closest(".modal").removeClass("show");
    $(".modal-scrim.show").removeClass("show");
    $("#notifications .notification.unread").removeClass("unread");
    e.stopPropagation();
  });

  $('.modal').on('click', '.js-ask-another', function(e) {
    e.preventDefault();
    $(this).closest(".modal").removeClass("show");
    $(".modal-scrim.show").removeClass("show");
    $('#search #search-query').focus();
  });

  $('.modal').on('click', '.js-facebook-share', function(e) {
    e.preventDefault();
    FB.ui({
      method: 'share',
      href: 'http://askplaybook.com',
    }, function(response){});
  });




  // replace failed profile pictures
  $('img[data-type=profile-picture]').on('error', function(){
      $(this).attr('src', 'https://s3-us-west-1.amazonaws.com/askplaybook/avatars/account-placeholder-square.png');
  });

});

