playbook.controller('ContributeController', ['$scope', 'Api', '$window', function($scope, Api, $window) {
    $scope.answer = {
        "question_id": null,
        "id": null,
        "overview": "",
        "action_items": [
            {
                "action": "",
                "context": ""
            }
        ],
        "is_actions_ordinal": false,
        "status": "draft",
        "url": ""
    };

    $scope.cancel_edit = function(question_id, user_id) {
      if ($scope.answer.status == 'published') {
        // reload old answer
        $scope.get_answer(question_id, user_id);
      }
    }

    $scope.get_answer = function(question_id, user_id) {
        $scope.answer.question_id = question_id;

        var payload = { question_id: question_id, user_id: user_id };
        Api.get_answer(payload).
        success(function(data, status, headers, config) {
            if (data != null) {
                $scope.answer = data;
                $scope.answer.action_items.push({ "action": "", "context": "" });
                setTimeout(function() {
                  $scope.$broadcast('elastic:adjust');
                }, 100);
            }
        }).
        error(function(data, status, headers, config) {
            // return false;
        });
    }

    $scope.delete_answer = function(question_id) {
      var payload = { question_id: question_id };
      Api.delete_answer(payload).
      success(function(data, status, headers, config) {
          $scope.answer = {
              "question_id": null,
              "id": null,
              "overview": "",
              "action_items": [
                  {
                      "action": "",
                      "context": ""
                  }
              ],
              "is_actions_ordinal": false,
              "status": "draft",
              "url": ""
          };
          $scope.edit_answer(false);
      }).
      error(function(data, status, headers, config) {
          // return false;
      });
    }

    $scope.push_action_item = function(index, current_item, last) {
        if (current_item.action != "" && last == true) {
            // push new action item
            $scope.answer.action_items.push({ "action": "", "context": "" });
        } else if ((current_item.action == undefined || current_item.action == "") && last == false) {
          if (current_item.context == undefined || current_item.context == "") {
            // remove action item because title is empty
            $scope.answer.action_items.splice(index, 1);
            $(".card-actions li:last-of-type .card-action-item").focus();
          }
        };

        if ($scope.answer.action_items.length > 1) {
            $scope.action_items_placeholder = "Action Item";
        } else {
            $scope.action_items_placeholder = default_action_items_placeholder;
        }
    }

    $scope.action_items_ordinal = function(is_actions_ordinal) {
      $scope.answer.is_actions_ordinal = is_actions_ordinal;
      $scope.card_form.$setDirty();
      if ($scope.answer.status == "draft") {
        $scope.save_card(false);
      }
    }

    $scope.save_card = function(publish) {
        if (publish) {
            $scope.edit_answer(false);
            // set card to published
            $scope.answer.status = "published";
        } else {
            if ($scope.answer.status == "published") {
                // don't autosave if not clicking publish and already published answer
                return;
            }
        }

        var payload = $scope.answer;

        // for(var i = 0; i < payload.action_items.length; i++) {
        //     if (payload.action_items[i].action == "") {
        //         payload.action_items.splice(i, 1);
        //     }
        // }

        Api.save_answer(payload).
        success(function(data, status, headers, config) {
            $scope.answer.id = data.id;
            $scope.answer.url = data.url;
            $scope.card_form.$setPristine();
            // update actions scope
            return true;
        }).
        error(function(data, status, headers, config) {
            return false;
        });
    }
}]);

