playbook.controller('QuestionController', ['$scope', 'Api', function($scope, Api) {
    $scope.question_id = null;
    $scope.question_loaded = false;
    $scope.perspective_options = [];
    $scope.perspectives = {
      "gender": {
        "previously_active": [],
        "selected": []
      },
      "job_type": {
        "previously_active": [],
        "selected": []
      },
      "company_type": {
        "previously_active": [],
        "selected": []
      },
      "company_size": {
        "previously_active": [],
        "selected": []
      }
    };

    $scope.actions = [];
    $scope.answers = [];
    $scope.contributors = [];
    $scope.people_follows = [];
    $scope.load_answers = function(current_user_exists, user_id) {
      var payload = {
        "question_id": $scope.question_id,
        "perspectives": $scope.perspectives
      }

      Api.load_answers(payload).
      success(function(data, status, headers, config) {
        $scope.actions = data.actions;
        $scope.answers = data.answers;

        $scope.contributors = data.contributors;
        $scope.perspective_options = data.perspectives;
        $scope.ads = data.ads;
        $scope.perspectives_selected = false; // if any perspective options are selected

        // loop through active options and add that to previously_active
        for (var i = 0; i < $scope.perspective_options.length; i++) {
          for (var j = 0; j < $scope.perspective_options[i].options.length; j++) {
            if ($scope.perspectives[$scope.perspective_options[i].perspective]["selected"].length > 0) {
              $scope.perspectives_selected = true; // a perspective option is selected
            }
            if (($scope.perspectives[$scope.perspective_options[i].perspective]["previously_active"].indexOf($scope.perspective_options[i].options[j].value) == -1) && ($scope.perspective_options[i].options[j].active)) {
              $scope.perspectives[$scope.perspective_options[i].perspective]["previously_active"].push($scope.perspective_options[i].options[j].value);
            } else {
              if (($scope.perspectives[$scope.perspective_options[i].perspective]["previously_active"].indexOf($scope.perspective_options[i].options[j].value) != -1) && (!$scope.perspective_options[i].options[j].active)) {
                var index = $scope.perspectives[$scope.perspective_options[i].perspective]["previously_active"].indexOf($scope.perspective_options[i].options[j].value);
                $scope.perspectives[$scope.perspective_options[i].perspective]["previously_active"].splice(index, 1);
              }
            }
          }
        }

        $scope.question_loaded = true;

        $scope.load_people_follows(current_user_exists); // load the people you follow

        return true;
      }).
      error(function(data, status, headers, config) {
        return false;
      });
    };

    $scope.select_perspective = function(p, option) {
      if (option.active) {
        if (option.selected) {
          var index = $scope.perspectives[p.perspective]["selected"].indexOf(option.value);
          $scope.perspectives[p.perspective]["selected"].splice(index, 1);
        } else {
          if ($scope.perspectives[p.perspective]["selected"].indexOf(option.value) == -1) {
            $scope.perspectives[p.perspective]["selected"].push(option.value);
          }
        }
        option.selected = !option.selected;
        $scope.load_answers();
      }
    }

    $scope.load_people_follows = function(no_current_user) {
      if(!no_current_user) {
        Api.load_people_follows().
        success(function(data, status, headers, config) {
            $scope.people_follows = data;
            $scope.set_followed_people();
            return true;
        }).
        error(function(data, status, headers, config) {
            return false;
        });
      }
    }

    $scope.set_followed_people = function() {
        for(var i = 0; i < $scope.people_follows.length; i++) {
            $scope.contributors.filter(function(q, j) {
              if(q.id == $scope.people_follows[i]) {
                $scope.contributors[j].is_followed = true;
              }
            });
        }
    }

    $scope.follow_profile = function(followed_id, no_current_user) {
        // no_current_user = false if logged in, true if logged out
        var payload = {
            "followed_id": followed_id
        }

        Api.follow_profile(payload).
        success(function(data, status, headers, config) {
          if (!no_current_user) {
            $scope.load_people_follows();

            $scope.contributors.filter(function(q, j) {
              if(q.id == followed_id) {
                $scope.contributors[j].is_followed = !$scope.contributors[j].is_followed;
              }
            });
          }
          return true;
        }).
        error(function(data, status, headers, config) {
            return false;
        });
    }
}]);

